async function loadTT(){
  try{const r=await fetch('./data.json');const d=await r.json();if(Array.isArray(d)&&d.length){STATE.timetable=d;save();return}}catch{}
  STATE.timetable=FALLBACK_TT;
}
async function loadQuiz(){
  // If we already have a populated bank (from localStorage or previous load), don't overwrite it
  if(STATE.quizQuestionsEdited && Array.isArray(STATE.quizQuestions) && STATE.quizQuestions.length) return;
  if(Array.isArray(STATE.quizQuestions) && STATE.quizQuestions.length>10) return;

  // 1) Try to load from questions.json (works when served via http(s))
  try{
    const r = await fetch('./questions.json', { cache: 'no-store' });
    const d = await r.json();
    if(Array.isArray(d) && d.length){
      STATE.quizQuestions = d;
      // Persist locally so Parent Dashboard edits apply to the quiz on next reload too
      saveLocal();
      return;
    }
  }catch{}

  // 2) Fallback: use embedded questions (works on file:// too)
  if(!Array.isArray(STATE.quizQuestions) || STATE.quizQuestions.length<=10){
    STATE.quizQuestions = Array.isArray(EMBED_QUIZ_QUESTIONS) ? EMBED_QUIZ_QUESTIONS.slice() : [];
    saveLocal();
  }
}

