function buyItem(id){
  const p=prf();if(!p)return;const item=SHOP_ITEMS.find(i=>i.id===id);if(!item)return;
  // FIX: Use gems only โ competition points NEVER reduced
  if((p.gems||0)<item.price){toast('๐ ุฌูุงูุฑู ุบูุฑ ูุงููุฉ!');return}
  if((p.inventory||[]).includes(id)){toast('โ ููููู!');return}
  p.gems=Math.max(0,(p.gems||0)-item.price);// FIX: prevent negative gems
  if(!p.inventory)p.inventory=[];p.inventory.push(id);
  if(item.id==='grace')p.graceTokens=(p.graceTokens||0)+1;
  if(item.id==='chest'){const bonus=30+Math.floor(Math.random()*70);p.gems=(p.gems||0)+bonus;const cm=document.getElementById('chest-msg');if(cm)cm.textContent='+'+bonus+' ุฌููุฑุฉ ููุงูุฃุฉ! ๐';showModal('m-chest')}
  if(item.id==='dates'){p.gems=(p.gems||0)+30}
  addActivityLog(p,'ุงุดุชุฑู '+item.name,-item.price,'approved');save();renderShop();toast('๐ ุงุดุชุฑูุช '+item.name+'!');
}

